//concept used - bottom up tabulation using dp
var minCost = function(n, cuts) {
    cuts.sort((a, b) => a - b) //this sorts the given array from start to end
    cuts = [0, ...cuts, n]//represents the position at which the cut needs to be made between 0 to n
    const stickLen = cuts.length //stick's length is assigned as the length of the cuts arr
    const dp = Array(stickLen).fill(0).map(() => Array(stickLen).fill(0))//base case is defined
    for (let i = stickLen - 2; i >= 0; i--){
        for (let j = i + 2; j < stickLen; j++){
            let minCost = Infinity;
            for (let k = i + 1; k < j; k++){
                //recurrence relation
                const cost = cuts[j] - cuts[i] + dp[i][k] + dp[k][j]
                //cuts[j] - cuts[i] represent length of stick being cut at that position
                //dp[i][k] represents the minimum cost of cutting the stick between positions cuts[i] and cuts[k]
                //dp[k][j] represents the minimum cost of cutting the stick between positions cuts[k] and cuts[j]
                minCost = Math.min(minCost, cost)
            }

            dp[i][j] = minCost//the dp is updated with the mincost
        }
    }
    return dp[0][stickLen - 1]//stores mincost in first row last element  
};
