/**
 * @param {number} n
 * @param {number[]} cuts
 * @return {number}
 */
var minCost = function(n, cuts) {    
    const cache = {};
    const fn = (start,end)=>{
        let key = `${start}:${end}`;
        if(cache[key] != undefined) return cache[key];
        if(start>=end) {            
            return 0;
        };
        // let minL = Infinity;
        let size = Infinity;                
        for(const c of cuts){
            if(start<c && c<end){
                size = Math.min((end-start) + fn(start,c) + fn(c,end),size)
            }
        }        
        cache[key] = size == Infinity ? 0 : size;
        return cache[key];
    }
    return fn(0,n);
};
