# Code
```
/* DP - Top Down */
func minCost(n int, cuts []int) int {
    // Make cuts as [0, cuts..., n]
    cuts = append([]int{0}, cuts...)
    cuts = append(cuts, n)
    sort.Ints(cuts)

    cache := make(map[string]int)
    return findMinCuts(0, len(cuts)-1, cuts, cache)
}

func findMinCuts(left, right int, cuts []int, cache map[string]int) int {
    if right - left == 1 {
        return 0 // single cut
    }

    key := strconv.Itoa(left) + "-" + strconv.Itoa(right)
    if value, ok := cache[key]; ok {
        return value
    }

    res := math.MaxInt64
    for mid := left+1; mid < right; mid++ {
        res = min(res, findMinCuts(left, mid, cuts, cache) + findMinCuts(mid, right, cuts, cache) + cuts[right] - cuts[left])
    }
    cache[key] = res
    return res
}

/* DP -- Bottom Up */
func minCost(n int, cuts []int) int {
    // Make cuts as [0, cuts..., n]
    cuts = append([]int{0}, cuts...)
    cuts = append(cuts, n)
    sort.Ints(cuts)

    m := len(cuts) // New length
    dp := make([][]int, m)
    for i := 0; i < m; i++ {
        dp[i] = make([]int, m)
    }

    for diff := 2; diff < m; diff++ {
        for left := 0; left < m - diff; left++ {
            right := left + diff
            res := math.MaxInt64
            for mid := left+1; mid < right; mid++ {
                res = min(res, dp[left][mid] + dp[mid][right] + cuts[right] - cuts[left])
            }
            dp[left][right] = res
        }
    }

    return dp[0][m-1]
}

func min(a, b int) int {
    if a < b {
        return a
    }
    return b
}
