impl Solution {
    pub fn min_cost(n: i32, mut cuts: Vec<i32>) -> i32 {
        //add "cuts" at positions 0 and n (required for dp approach)
        cuts.push(0);
        cuts.push(n);

        //sort, so that neighboring positions in this list require no cuts between them
        cuts.sort();

        let mut dp_data = vec![vec![0; cuts.len()]; cuts.len()];

        Solution::dp(0, cuts.len() - 1, &cuts, &mut dp_data)
    }

    //Dynamic programming with memoization
    //returns the cost to add all required cuts to 
    //the (sub)bar that starts at cuts[idx1] and ends at cuts[idx2]
    fn dp(idx1: usize, idx2: usize, cuts: &Vec<i32>, dp_data: &mut Vec<Vec<i32>>) -> i32 {
        if idx2 - idx1 == 1 || dp_data[idx1][idx2] > 0 {
            //solution is 0 or we already have a solution
            return dp_data[idx1][idx2];
        }
        let mut ans = 2000000000; //some large value
        for cut_idx in (idx1 + 1)..idx2 {
            //find the lowest cost cut in all cuts to be placed on the (sub)bar
            ans = std::cmp::min(ans, Solution::dp(idx1, cut_idx, cuts, dp_data) 
                                    + Solution::dp(cut_idx, idx2, cuts, dp_data));
        }
        dp_data[idx1][idx2] = ans + cuts[idx2] - cuts[idx1];
        dp_data[idx1][idx2]
    }
}
```

