            dp[i][i+1]=0;
        for(int i=0;i<m-2;i++)
            dp[i][i+2]=cuts[i+2]-cuts[i];
        for(int i=1;i<m;i++)
        {
            int k=0;
            for(int j=i+1;j<m;j++,k++)
            {
                int cost=INT_MAX;
                for(int u=k+1;u<j;u++)
                {
                     //cout<<k<<" "<<j<<" "<<u<<endl;
                    cost=min(cost,cuts[j]-cuts[k]+dp[k][u]+dp[u][j]);
                }
                dp[k][j]=min(dp[k][j],cost);
            }
        }

        return dp[0][m-1];

    }
