class Solution {
public:
    map<pair<int,int>,int>mp;
    int solve(vector<int>&cuts, int start,int end,int i, int j){
        if(i>j or end-start==0)
            return 0;
        if(mp.find({i,j})!=mp.end()){
            return mp[{i,j}];
        }
        if(i==j )
            return mp[{i,j}]= end-start;
        // cout<<start<<" "<<end<<" "<<cuts[i]<<" "<<cuts[j]<<" "<<i<<" "<<j<<endl;
        int ans=INT_MAX;
        for(int cut=i;cut<j+1;cut++){
            int val = (end-start)+solve(cuts,start,cuts[cut],i,cut-1)+solve(cuts,cuts[cut],end,cut+1,j);
            ans =min(ans,val);
        }
        return mp[{i,j}]= ans;
    }
    int minCost(int n, vector<int>& cuts) {
        int len =cuts.size();
        sort(cuts.begin(),cuts.end());
        mp.clear();
        return solve(cuts,0,n,0,len-1);
    }
};
