private static int solve(ArrayList<Integer> arr, int i, int j){
        if(i > j) return 0;
        int ans = Integer.MAX_VALUE;
        for(int k=i;k<=j;k++){
            int temp = solve(arr, i, k-1) + solve(arr, k+1, j) + arr.get(j+1) - arr.get(i-1);
            ans = Math.min(ans, temp);
        }
        return dp[i][j] = ans;
