    private static int solveTab(ArrayList<Integer> arr, int m){
        int[][] dp = new int[m+2][m+2];

        for (int i = m; i >= 1; i--) {
            for (int j = 1; j <= m; j++) {
                if(i > j) continue;
                int ans = Integer.MAX_VALUE;
                for(int k=i;k<=j;k++){
                    int temp = dp[i][k-1] + dp[k+1][j] + arr.get(j+1) - arr.get(i-1);
