        int temp=INT_MAX;
        for(int i=s+1;i<e;i++){
            temp=min(temp,cuts[e]-cuts[s]+find(s,i,cuts,dp)+find(i,e,cuts,dp));
        }
        dp[s][e]=temp;
        return temp;
    }

    int minCost(int n, vector<int>& cuts) {
        vector<int> cut(1,0);
        sort(cuts.begin(),cuts.end());
        for(int i=0;i<cuts.size();i++){
