        cut.push_back(n);
        int n2=cut.size();
        vector<vector<int>> dp(cut.size(),vector<int> (cut.size(),-1));
        for(int i=0;i+1<n2;i++) dp[i][i+1]=0;
        return find(0,n2-1,cut,dp);
    }
};

