/*
    Intuition:

    Memoisation:

    Step 1:
    Let us consider that the partition array is our cuts array

    Step 2:
    We insert first index (0) and last index (n) to this array so that 
    we shall be able to find the lengths of the current state of the stick at any given left and right ends 

    Step 3:
    Now, we represent it in terms of indexes, as left and right most ends of stick

    Base Case: if left end goes beyond right ends (Return 0 in this case)

    Step 4: 
    Now, to explore all possibilities:

    All possible partitions in sorted order can be made through a loop of possibilities 
    to consider all possible partitions at any given stage

    Here, we need to calculate the lengths of both the partitions in summation with current length
    i.e. previous cut from right end - previous cut from left end

    Step 5:
    Find the minimum of all this
    and store in our dp array for current combination of i, j
    and return it


    Tabulation:

    We simply use the memoization to write tabulation

    Step 1:
    Copy the base case (Basically initialise the dp array of size (m+2, m+2) with all 0s)
    because we added two values, i.e. 0 and n to our partition array

    Step 2:
    Move from bottom to up, i.e. consider that we reached the last case, i.e. i = m
    and j is at 1 and traverse for each possible combination

    Step 3:
    Now, from i to j (inclusive) put partition and calculate the cost for each side followed by considering the minimum cost

    Step 4: 
    Return your answer stored at 1, m because cuts actual size was m and 0th index doesn't belong to the cuts vector
*/

class Solution {
public:

    int memSoln(int i, int j, vector<int> &cuts, vector<vector<int>> &dp)
    {
        if(i > j)
            return 0;

        if(dp[i][j] != -1)
            return dp[i][j];

        int ans = 1e9;
        for(int k = i; k <= j; k++)
            ans = min(ans,  memSoln(i, k-1, cuts, dp) + memSoln(k+1, j, cuts, dp) + cuts[j+1] - cuts[i-1]);         

        return dp[i][j] = ans;
    }

    int tabulation(int n, int m, vector<int> &cuts)
    {
        vector<vector<int>> dp(m+2, vector<int>(m+2, 0));

        for(int i = m; i >= 1; i--)
        {
            for(int j = 1; j <= m; j++)
            {
                if(i <= j) 
                {
                    int ans = 1e9;
                    for(int k = i; k <= j; k++)
                        ans = min(ans,  dp[i][k-1] + dp[k+1][j] + cuts[j+1] - cuts[i-1]);         

                    dp[i][j] = ans;
                }
            }
        }

        // for(int i = 0; i < m+2; i++)
        // {
        //     for(int j = 0; j < m+2; j++)
        //         cout<<dp[i][j]<<" ";
        //     cout<<endl;
        // }

        return dp[1][m];
    }

    int minCost(int n, vector<int>& cuts) 
    {
        int m = cuts.size();
        cuts.push_back(n);
        cuts.insert(cuts.begin(), 0);
        sort(cuts.begin(), cuts.end());

        // vector<vector<int>> dp(101, vector<int>(101, -1));
        // return memSoln(1, m, cuts, dp);

        return tabulation(n, m, cuts);
    }
};

