class Solution {
public:
    map<pair<int,int>,int>mp;
    int minCost(int n, vector<int>& cuts) {

        int start=0;
        int end=n;

        int left=0;
        int right=cuts.size()-1;

        sort(cuts.begin(),cuts.end());   

        return fun(cuts,start,end,left,right);
    }
    int fun(vector<int>&cuts,int start,int end,int left,int right)
    {
        if(left>right)  
        {
            return 0;
        }
        if(mp.find(make_pair(start,end))!=mp.end())   
        {
            return mp[make_pair(start,end)];
        }
        int ans=INT_MAX;

        for(int i=left;i<=right;i++)
        {

           ans=min(ans,(end-start)+fun(cuts,start,cuts[i],left,i-1)+fun(cuts,cuts[i],end,i+1,right));    
        }
        return mp[make_pair(start,end)]= ans;   
    }
};
