	bool canJump(vector<int>& nums) {
        int n = nums.size();
        return f(0, n, nums);
	}

	bool f(int ind, int n, vector<int>& nums) {
        if(ind == n-1) return true;

        int jump = nums[ind];
        bool check = false;
        for(int i = ind+1; i <= min(n-1, ind+jump); i++)
            check = check || f(i, n, nums);

        return check;
	}
