class Solution(object):
    def canJump(self, nums):
        max_reach, i, N = 0, 0, len(nums)
        while i < N and max_reach >= i:
            max_reach = max(max_reach, i+nums[i])
            if max_reach >= N-1:
                return True
            i += 1
        return False
