from functools import wraps
class Solution:
    def canJump(self, nums):
        """
        :type nums: List[int]
        :rtype: bool
        """
        if not nums: return True
        def memo(func):
            cache = {}
            @wraps(func)
            def wrap(*args):
                if args not in cache:
                    cache[args]=func(*args)
                return cache[args]
            return wrap
        @memo
        def solve(idx):
            if idx == len(nums)-1: 
                return True
            if idx >= len(nums):
                return False
            return any([solve(idx+jumpDist) for jumpDist in range(1,nums[idx]+1)])

        return solve(0)
