class Solution {
public:

    bool canJump(vector <int> nums, int curr) {
        if (curr == nums.size() - 1) {
            return true;
        }
        for (int i = nums[curr]; i > 0; i--) {
            if (canJump(nums, curr + i)) {
                return true;
            }
        }
        return false;
    }


    bool canJump(vector<int>& nums) {
        return canJump(nums, 0);
