        int n = nums.size();
        vector <bool> dp(n, false);
        dp[n - 1] = true;

        for (int curr = n - 2; curr >= 0; curr--) {    
            int last = min(n - 1, curr + nums[curr]);

            for (int next = curr + 1; next <= last; next++) {
                if (dp[next]) {
                    dp[curr] = true;
                    break;
                }
            }
        }

        return dp[0];
    }
};
