class Solution {
    public boolean canJump(int[] nums) {
        int n = nums.length;
        Queue<Integer> q = new ArrayDeque<>();
        q.offer(0);
        int maxReach=-1;
        while(!q.isEmpty()) {
            int size = q.size();
            for(int i =0;i<size;i++) {
                int v = q.poll();
                if (v == n-1) return true;
                 //children
                int v_reach = nums[v]+v;
                for(int c = maxReach+1; c<=Math.min(v_reach,n-1) ;c++) {
                        q.offer(c);
                }      
                maxReach = Math.max(maxReach,v_reach);
            }   
        }
        return false;
    }
}
