```
class Solution {
public:
    int dp[100001];//intialsation of value

    bool solve(int s,int n, vector<int>&nums)
    {
        if(s>=n)
        {
		// if we able to reach the end point of array ie: `n-1` return true
            return dp[s]=1;
        }
        if(s>n)
        {
            return dp[s]=0;//if we reach  beyond the end point of array ie:(n,n+1,n+2......)return false
        }
		//if answer is already present in dp array we only return it
        if(dp[s]!=-1)
        {
            return dp[s];
        }
		// now we check every possible positon and combination=>(s+i)
        for(int i=1;i<=nums[s];i++)
        {
            if(solve(s+i,n,nums))
            {
                return dp[s]=1;
            }

        }
         return dp[s]=0;
    }


    bool canJump(vector<int>& nums) {
        int n=nums.size();
		//intitalstaion of dp
        for(int i=0;i<100001;i++)
        {
            dp[i]=-1;
        }
		// recursion start===>start with intial value 0 and reach at n-1
        return solve(0,n-1,nums);


    }
};
```

