    {
         if( index==n-1)
      { 
          return true ;

      }


    if( index >=n || nums[index]==0)
    {
         return false ;
    }

    if( ans[index]!=-1)
    {

     return ans[index];    
    }

 bool b = false ; 

for( int j =1 ; j<=nums[index] ; j++)
{
    b = b+ isHelp(nums , index+j,n, ans);

     if( b)
     {
          break;
     }

}
    ans[index] = b; 
    return  b ;     
    }

    bool canJump(vector<int>& nums) 
      {
    int n = nums.size();
    vector<int> ans(n ,-1);
    return isHelp(nums ,0 ,n,ans);


    }
};

