public class MinStack {

    private Stack<int> stack1;
    private Stack<int> stack2;

    /** initialize your data structure here. */
    public MinStack() {
        stack1 = new Stack<int>();
        stack2 = new Stack<int>();
    }

    public void Push(int x) {
        if(stack1.Count == 0)
        {         
            stack1.Push(x);
            stack2.Push(x);
        }
        else
        {
            stack1.Push(x);
            int currMin = stack2.Peek();
            stack2.Push(Math.Min(x, currMin));
        }
    }

    public void Pop() {
        stack1.Pop();
        stack2.Pop();
    }

    public int Top() {
        return stack1.Peek();
    }

    public int GetMin() {
        return stack2.Peek();    
    }
}
```

