        else:
            self.min.append(self.min[-1])
        self.stack.append(x)

    def pop(self) -> None:
        self.stack = self.stack[:-1]
        self.min = self.min[:-1]

    def top(self) -> int:
        return self.stack[-1]

    def getMin(self) -> int:
        if not self.min: return None
        return self.min[-1]
