class MinStack {
public:
    struct Node
    {
        int num;
        Node *next;
        int min;
    };
    struct Node *head=NULL;
    MinStack() {
    }

    void push(int val) {
        Node *temp = new Node();
        temp->num=val;
        temp->min=val;
        temp->next=NULL;
        if(!head)
        {
            head=temp;
        }
        else
        {
            temp->min = min(temp->min, head->min);
            temp->next=head;
            head=temp;
        }
    }

    void pop() {
        Node *temp = head;
        head=head->next;
        //delete(temp);
    }

    int top() {
        return head->num;
    }

    int getMin() {
        return head->min;
    }
};

