class MinStack 
{

    Stack<Long> stack = new Stack<>();
    Long mini;

    public MinStack() 
    {
        mini = Long.MAX_VALUE;
    }

    public void push(int val) 
    {
        Long value = Long.valueOf(val);
        if(stack.isEmpty())
        {
            mini =  value;
            stack.push(value);
        }
        else
        {
            if(value < mini)
            {
                stack.push(2 * value - mini);
                mini = value;
            }
            else
            {
                stack.push(value);
            }
        }
    }

    public void pop() 
    {
        if(stack.isEmpty())
        {
            return;
        }

        Long value = stack.pop();
        if(value < mini)
        {
            mini = 2 * mini - value;
        }

    }

    public int top() 
    {
        Long value = stack.peek();
        if(value < mini)
        {
            return mini.intValue();
        }
        return value.intValue();
    }

    public int getMin() 
    {
        return mini.intValue();
    }
}

