class Solution:
    def longestStrChain(self, words: List[str]) -> int:
        ans = 0
        words_set = set(words)
        dp = {}
        def dfs(word):
            if word in dp: return dp[word]
            ans = 1
            for i in range(len(word)):
                next_word = word[:i] + word[i + 1:]
                if next_word in words_set:
                    ans = max(1 + dfs(next_word), ans)
            dp[word] = ans
            return ans

        for word in words:
            ans = max(ans, dfs(word))
        return ans

