from collections import defaultdict
class Solution(object):
    def longestStrChain(self, words):
        """
        :type words: List[str]
        :rtype: int
        """
        len_dict = defaultdict(list)
        for word in words:
            len_dict[len(word)].append(word)

        graph = defaultdict(list)


        def is_pre(word1, word2):
            i, j = 0, 0
            found = False
            while i < len(word1) and j < len(word2):
                if word1[i] != word2[j]:
                    if found:
                        return False
                    else:
                        found = True
                        j += 1
                        continue
                i += 1
                j += 1

            return True

        for word in words:
            for word1 in len_dict[len(word) + 1]:
                if is_pre(word, word1):
                    graph[word].append(word1)

        self.hash_map = {}
        def dfs(word):
            count = 0
            if word in self.hash_map:
                return self.hash_map[word]
            for word1 in graph[word]:
                count = max(count, 1 + dfs(word1))

            self.hash_map[word] = count
            return count
        ans = 0
        for word in words:
            ans = max(ans, 1 + dfs(word))

        return ans

