                tmp = cur[:j]+cur[j+1:]

                if tmp in dic:
                    cnt = max(cnt, dic[tmp]+1)
            if cnt == float('-inf'):
                cnt = 1
            dp[i] = cnt
            dic[cur] = cnt

        return max(dp)

