class Solution {
public:
    bool isSubseq(string &s1, string &s2){
        // to check whether s1 is subsequence of s2
        int i=0,j=0;
        while(i<s1.length() and j<s2.length())
        {
            if(s1[i]==s2[j]){
                i++;
                j++;
            }
            else
                j++;
        }
        return (i==s1.length());
    }

    // written a function to sort the words vector according to the length
    static bool bylength(string &s1, string &s2){
        return s1.length()<s2.length();
    }

    int longestStrChain(vector<string>& words) {
        int n=words.size();
        sort(words.begin(), words.end(), bylength);

        int res=1; // since every word will form a chain of itself so when no chain forms answer would be 1
        int longestBeforeHere[n];  // array to store the longest chain formed before the ith point (and including it) at index i
        longestBeforeHere[0] = 1;  // every word forms 1 length chain

        for(int i=1;i<words.size();i++){
            int curr=1;
            for(int j=i-1;j>=0;j--){
                if(words[i].length()==words[j].length()+1 and isSubseq(words[j],words[i])) // condition to increase chain size
                    curr=max(curr,longestBeforeHere[j]+1); // update the length of longest chain starting with i and before it
            }
            longestBeforeHere[i]=curr; // we store the value of curr at ith index for future use
            res=max(res,curr); // every time we update res by finding the final maximum value
        }

        return res;
    }
};
