class Solution {
	static bool compare(string s1 , string s2) {
		return s1.length() < s2.length();
	}

	bool isPredecessor(string &s1, string &s2) {
		int i = 0, j = 0;
		bool flag = false;

		while(i < s1.length() and j < s2.length()) {
			if(s1[i] != s2[j]) {
				if(flag)
					return false;
				i++;
				flag = true;
			} else {
				i++;
				j++;
			}
		}
		return (flag and i == s1.length() and j == s2.length()) or (!flag and i == (s1.length() - 1) and j == s2.length());
	}
public:
	int longestStrChain(vector<string>& words) {
		vector<int> mem(words.size(), 1);
		int res = 0;

		sort(words.begin(), words.end(), compare);
		for(int i = 1; i < words.size(); i++) {
			int len = words[i].length();
			for(int j = i - 1; j >= 0; j--) {
				if((len - words[j].length()) > 1)
					break;

				if(isPredecessor(words[i], words[j]))
					mem[i] = max(mem[i], mem[j] + 1);
			}
			res = max(res, mem[i]);
		}
		return res;
	}
};

