class Solution:
    def longestStrChain(self, words: List[str]) -> int:
        # Choices -> notpick ; found LCS => pick , notpick
        words.sort(key=len)
        def LCS(i, j, s1, s2, memo):
            if len(s2) - len(s1) != 1:
                memo[(i, j)] = False
            if (i, j) in memo:
                return memo[(i, j)]
            if i < 0:
                memo[(i, j)] = True
            elif j < 0 and i >= 0:
                memo[(i, j)] = False
            elif s1[i] == s2[j]:
                memo[(i, j)] = LCS(i-1, j-1, s1, s2, memo)
            else:
                memo[(i, j)] = LCS(i, j-1, s1, s2, memo)
            return memo[(i, j)]

        dp = [1] * len(words)
        for i in range(len(words)):
            for prev in range(i):
                m = len(words[prev])
                n = len(words[i])
                memo = {}
                if LCS(m -1, n-1, words[prev], words[i], memo):
                    dp[i] = max(dp[i], 1 + dp[prev])
        return max(dp)






