class Solution:
    def longestStrChain(self, words: List[str]) -> int:
        memo = {}
        available_words = set(words)
        MAX = 1
        for word in words:
            if word not in memo:
                MAX = max(MAX, self.recurse(words, word, available_words, memo))
        return MAX

    def recurse(self, words: List[str], word: str, available_words, memo):
        if len(word) == 1:
            memo[word] = 1
            return 1

        if word in memo:
            return memo[word]

        MAX = 1
        for i in range(len(word)):
            possible_predecessor = word[:i] + word[i+1:]
            if possible_predecessor in available_words:
                MAX = max(MAX, self.recurse(words, possible_predecessor, available_words, memo) + 1)

        memo[word] = MAX
        return MAX
