			for(int idx1 = 1; idx1 <= m; idx1++) {
				for(int idx2 = 1; idx2 <= n; idx2++) {
					if(word1[idx1 - 1] == word2[idx2 - 1])
						dp[idx1][idx2] = 1 + dp[idx1 - 1][idx2 - 1]; 
					else 
						dp[idx1][idx2] = max(dp[idx1 - 1][idx2], dp[idx1][idx2 - 1]);
				}
			}
			return dp[m][n];
