        sort(words.begin(), words.end(), cmp);
        vector<int> lis(words.size(), 1);
        int ans = 1;
        for (int i = 1; i < words.size(); i++)
            for (int j = 0; j < i; j++) 
                if (preceds(words[j], words[i])) {
                    lis[i] = max(lis[i], lis[j]+1);
                    ans = max(ans,lis[i]);                    
                }
        return ans;
    }
