    bool preceds(string &predecessor, string &word) {
        if (predecessor.length() != word.length()-1)
            return false;
        int first = 0, second = 0, misMatches = 0;
        while (first < predecessor.length() && misMatches <= 1) 
            if (predecessor[first] != word[second]) {
                second++;
                misMatches++;
            } else {
                first++;
                second++;
            }
        return misMatches <= 1;

    }

