int longestStrChain(vector<string>& words) {
        sort(words.begin(), words.end(), [](string &a, string &b){
            return (b.size() > a.size()) ? (true) : (false);
        });

        unordered_map<string, int> tbl;
        int ret = 1;
        for (auto s:words) {
            for (int i = 0; i < s.size(); i++) {
                string tmp = s.substr(0, i) + s.substr(i+1, s.size() - i);
                if (tbl.count(tmp))
                    tbl[s] = max(tbl[s], tbl[tmp] + 1);
            }
            tbl[s] = max(tbl[s], 1);
            ret = max(ret, tbl[s]);
        }
        return ret;
    }
