class Solution {

    private func findSubstrings(_ word: String) -> [String] {
        var subStrings = [String]()

        for removeIndex in 0..<word.count {
            var word = word
            word.remove(at: word.index(word.startIndex, offsetBy: removeIndex))
            subStrings.append(word)
        }

        return subStrings
    }

    func longestStrChain(_ words: [String]) -> Int {
        var map = Dictionary<String, Int>()
        var maxLength = 0, words = words.sorted()

        for word in words {
            let subStrings = findSubstrings(word)
            var longest = 0
            for subString in subStrings {
                longest = max(longest, map[subString, default: 0] + 1)
            }
            map[word] = longest
            maxLength = max(maxLength, longest)
        }
        return maxLength
    }
}

