class Solution {
public:
    bool check(string a,string b){
        int count = 0;
        int index = 0;
        for(int i = 0 ; i < a.size() and index < b.size(); i++,index++){
            if(a[i] != b[index]){
                count++;
                i--;
            }
            if(count > 1) return false;
        }
        return count <= 1;
