                if(words[j].size() == words[i].size() - 1)
                    if(check(words[j],words[i]))
                        dp[i] = max(dp[j] + 1,dp[i]);
        for(int i = 0 ; i < words.size() ; i++)
            ans = max(ans,dp[i]);
        return ans;
    }
};
