        if(!map.containsKey(s)) return 0;
        if(map.get(s) != 0) return map.get(s);

        int res = 1;
        for(int j = 0; j < s.length(); j++) {
            StringBuilder sb = new StringBuilder(s);
            res = Math.max(res, 1 + dfs(sb.deleteCharAt(j).toString(), map));
        }

        map.put(s, res);
        return res;
    }
}
