class Solution {
public:
    int dp[1000]; // An array to memoize intermediate results

    // A static comparison function for sorting words by length
    static bool mysort(string &a, string &b) {
        return a.length() < b.length();
    }

    // Function to check if two strings can form a valid chain
    bool check(string &a, string &b) {
        if (a.length() != b.length() - 1) return false;
        int count = 0;
        int i = 0, j = 0;
        while (i < a.length() && j < b.length()) {
            if (a[i] != b[j]) {
                if (count == 1) return false;
                count = 1;
            } else {
                i++;
            }
            j++;
        }
        return true;
    }

    // Recursive memoization function to find the longest chain
    int memo(int i, vector<string> &words, int n) {
        if (i >= n) return 0; // Base case: If we are out of words, return 0
        if (dp[i] != -1) return dp[i]; // If result is already memoized, return it
        int ans = 0;
        for (int j = i + 1; j < n; j++) {
            if (check(words[i], words[j])) {
                ans = max(ans, 1 + memo(j, words, n)); // Recursively find chain length
            }
        }
        return dp[i] = ans; // Memoize and return the result
    }

    // Main function to find the length of the longest string chain
    int longestStrChain(vector<string> &words) {
        sort(words.begin(), words.end(), mysort); // Sort words by length
        int n = words.size();
        int ans = 0; // Initialize the maximum chain length to 0

        // Iterate through each word and find the maximum chain length
        for (int i = 0; i < n; i++) {
            memset(dp, -1, sizeof dp); // Initialize the memoization array
            ans = max(ans, memo(i, words, n)); // Update the maximum chain length
        }
        return ans + 1; // Return the length of the longest chain
    }
};

```

