func longestStrChain(words []string) int {


    wordMap := make(map[string]int, len(words))

    // note the syntax
    sort.Slice(words, func(i, j int) bool {
        if len(words[i]) > len(words[j]) {
            return true
        }
        return false
    })

    // the initial chain for every word is 1 and we have it in a map for easy lookup
    for _, word := range words {
        wordMap[word] = 1
    }

    // for every word, I store the longest chain till that word, as the words are sorted in descending order
    // example: bdca - 1 | bda - 2 | bca - 2 | ba - 3 | b - 4 | a -4
    longestChain := 0
    for _, word := range words {

        for i:=0; i<len(word); i++ {
            newWord := word[:i] + word[i+1:]

            if _, ok := wordMap[newWord]; ok {
                wordMap[newWord] = max(wordMap[word]+1, wordMap[newWord])
            }
        }

        // to hold the max among all words, and we are traversing all the words so we definitely get the max
        longestChain = max(wordMap[word], longestChain)
    }

    return longestChain
}

func max (a,b int) int {
    if b > a {
        return b
    }
    return a
}

