                dict.Add(l, new List<string>());
            dict[l].Add(word);
        }
        Dictionary<string, List<string>> graph = new();
        foreach(string word in words)
        {
            BuildGraph(word, graph, dict);
        }

Dictionary<string, int> heights = new();
        foreach(string word in words)
        {
            Dfs(word, heights, graph);
        }
        return result;
