    private int Dfs(string current, Dictionary<string, int> heights, Dictionary<string, List<string>> graph)
    {
        if(heights.ContainsKey(current))
            return heights[current];
        heights.Add(current, 1);
        foreach(string child in graph[current])
        {
            int height = 1 + Dfs(child, heights, graph);
            heights[current] = Math.Max(heights[current], height);
        }
        if(heights[current] > result)
            Console.WriteLine(current);
        result = Math.Max(heights[current], result);
        return heights[current];
    }

    private void BuildGraph(string current, Dictionary<string, List<string>> graph, Dictionary<int, List<string>> dict)
    {
        if(graph.ContainsKey(current))
            return;
        int nextLen = current.Length + 1;
        graph.Add(current, new());
        if(!dict.ContainsKey(nextLen))
            return;
        foreach(string nxt in dict[nextLen])
        {
            if(CheckPredecessor(current, nxt))
            {
                graph[current].Add(nxt);
            }
        }    
    }

    private bool CheckPredecessor(string a, string b)
    {
        if(a.Length != b.Length - 1)
            return false;
        bool flag = false;
        int i = 0;
        while(i < a.Length)
        {
            if(flag)
            {
                if(a[i] != b[i+1])
                    return false;
                i++;
            }
            else
            {
                if(a[i] != b[i])
                    flag = true;
                else
                    i++;
            }
        }

        return a.Last() == b.Last() || !flag;
    }
}
