class Solution {
public:
    bool isValid(vector<string> &words, int prev, int curr) {
        if(prev == -1) return true;
        string s1 = words[prev];
        string s2 = words[curr];

        if((s1.size() + 1) == s2.size()) {
            int i = -1, j = -1, cnt = 0;
            for(i = 0, j = 0; i < s1.size() && j < s2.size(); ) {
                if(s1[i] == s2[j]) {
                    i++;
                    j++;
                    continue;
                }
                else if(s1[i] != s2[j]) {
                    j++;
                    cnt++;
                    if(cnt > 1) return false;
                }
            }
            if(i >= s1.size()) return true;
        }
        return false;
    }
    int solve(vector<string> &words, int prev, int curr, vector<vector<int>> &dp) {
        if(curr == words.size()) return 0;

        if(dp[prev + 1][curr] != -1) return dp[prev + 1][curr];

        int pick = 0;
        if(prev == -1 || isValid(words, prev, curr)) {
            pick = 1 + solve(words, curr, curr + 1, dp);
        }

        int notPick = 0 + solve(words, prev, curr + 1, dp);

        return dp[prev + 1][curr] = max(pick, notPick);
    }
    int longestStrChain(vector<string>& words) {
