class Solution {
    public int minOperations(int n) {
        int count =0;
        int[] arr = new int[n];
        for (int i=0; i < n ; i++){
            arr[i] =(2 * i) +1;
        }
        int left =0; 
        int right=n-1;
        while (left <= right){
            while (arr[left] != arr[right]){
             arr[left] += 1;
             arr[right] -= 1;  
                count++;
            }
            left++;
            right--;
        }
        return count;

    }
}
