/*
  linear function: 2n + 1
  target = sum(1 to f(n)) / n

  nth element (last elements value) = 2 * (n - 1) + 1
  target = avg of first and nth element: (2 * (n - 1) + 1 + 1) / 2
  simplify target => (2n - 2 + 2) / 2, which is = n;
*/
var minOperations = function (n) {
  let counter = 0;
  for (let i = 1; i < n; i += 2) {
    counter += n - i;
  }
  return counter;
}
