class Solution {
public:
    int minOperations(int n) {
        int dp[10001] = {0};
        dp[1] = 0;
        dp[2] = 1;
        dp[3] = 2;

        for(int i = 4;i<=n;i++){
            if(i%2 == 0){
                dp[i] = dp[i-1]+ (i/2);
            }else{
                dp[i] = dp[i-2] + (i-1);
            }
        }
        return dp[n];
    }
};

