class Solution {
public:
    unordered_map<char,char> par;
    int find(int x){
        if(par[x] == 0) return x;
        int temp = par[x];
        while(par[temp]){
            temp = par[temp];
        }
        return temp;
    } 
    void Union(int x, int y) {
        x = find(x);
        y = find(y);   
        if (x != y) {
            par[max(x, y)] = min(x , y);
        }
    }
    string smallestEquivalentString(string s1, string s2, string baseStr) {
        for (int i = 0; i < s1.size(); ++i) 
            Union(s1[i], s2[i]);
        for(char& c: baseStr) c = find(c); 
        return baseStr;
    }
};
