class Solution {
    public int find(int ch, int[] parent)
    {
        if(parent[ch] == -1)
            return ch;
        return find(parent[ch], parent);
    }
    public void Union(int ch1, int ch2, int[] parent)
    {
        int p_ch1 = find(ch1, parent);
        int p_ch2 = find(ch2, parent);

        if(p_ch1 != p_ch2)
        {
            int max_p = Math.max(p_ch1, p_ch2);
            int min_p = Math.min(p_ch1, p_ch2);

            parent[max_p] = min_p;
        }

    }
    public String smallestEquivalentString(String s1, String s2, String baseStr) {
        int parent[] = new int[26];
        for(int i = 0; i < 26; i++)
        {
            parent[i] = -1;
        }
        int n = s1.length();
        for(int i = 0; i < n; i++)
        {
            Union(s1.charAt(i)-'a', s2.charAt(i)-'a', parent);
        }
        n = baseStr.length();
        String ans = new String();
        for(int i = 0; i < n; i++)
        {
            char ch = (char) (find(baseStr.charAt(i)-'a', parent) + 'a');

            ans += ch;
        }
        return ans;
    }
}
