        if(par[a] == a) return a;
        return par[a] = getpar(par[a]);
    }
    void dsjoint(int a,int b){
        a = getpar(a);
        b = getpar(b);
        if(a < b) par[b] = a;
        else par[a] = b; 
    }
    string smallestEquivalentString(string s1, string s2, string baseStr) {
